import React, { useState, useEffect } from 'react';
import CardDaftarMenu from './CardDaftarMenu';
import axios from 'axios'

const DaftarMenu = ({aktif}) => {

	const [kategoriMenu, setKategoriMenu] = useState();

	useEffect(() => {
		axios
			.get("http://localhost:3004/products?category.nama=" + aktif)
			.then((response) => {
				// handle success
				setKategoriMenu(response.data)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, [aktif])

	const tambahPesan = (kkk) => {
		console.log(kkk)
	}

	return(
		<div className="container-cards">
			{kategoriMenu && kategoriMenu.map(kategori => {
				return(
					<div key={kategori.id} onClick={() => {tambahPesan(kategori.nama)}}>
						<CardDaftarMenu
							onClick={() => {tambahPesan("ramadhan")}}
							gambar={kategori.gambar}
							harga={kategori.harga}
							kode={kategori.kode}
							nama={kategori.nama}
							aktif={aktif}
						/>
					</div>
				)
			})}
		</div>
	)
}

export default DaftarMenu;
