import React, { useState, useEffect } from 'react';
import { Col, ListGroup } from 'react-bootstrap';
import axios from "axios";
import { API_URL } from "../utils/constants";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUtensils, faCoffee, faCheese } from '@fortawesome/free-solid-svg-icons';

const Icon = ({nama}) => {
	if (nama === "Makanan") { return <FontAwesomeIcon icon={faUtensils} className="mr-2"/>}
	if (nama === "Minuman") { return <FontAwesomeIcon icon={faCoffee} className="mr-2"/>}
	if (nama === "Camilan") { return <FontAwesomeIcon icon={faCheese} className="mr-2"/>}

	return <FontAwesomeIcon icon={faCheese} className="mr-2"/>
}

const ListKategori = (changeCategories, selectedCategories) => {

	const [ categories, setCategories ] = useState([])

	useEffect(() => {
		axios
			.get(API_URL + "categories")
			.then((response) => {
				// handle success
				setCategories(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, []);

	return(
		<Col md={3} mt="2">
			<h4><strong>Daftar Kategori</strong></h4>
			<hr />
			<ListGroup>
				{categories && categories.map((categori) => (
					<ListGroup.Item key={categori.id}>
						<h5>
							<Icon nama={categori.nama}/>{categori.nama}
						</h5>
					</ListGroup.Item>
				))}
			</ListGroup>
		</Col>
	)
}

export default ListKategori;
