import React, { useState, useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap";
import { NavbarComp, ListKategori, Hasil, Menus } from "./component/index";
import { API_URL } from "./utils/constants";
import axios from "axios";

const App = () => {
	const [menus, setMenus] = useState();
	const [selectedCategories, setSelectedCategories] = useState("Makanan");

	useEffect(() => {
		axios
			.get(API_URL + "products?category.nama=" + selectedCategories)
			.then((response) => {
				// handle success
				setMenus(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, []);

	const changeCategories = (value) => {
		setSelectedCategories(value)
		setMenus()

		axios
			.get(API_URL + "products?category.nama=" + value)
			.then((response) => {
				// handle success
				setMenus(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
		});
	}

	return (
		<>
			<div className="App">
				<NavbarComp />
				<div className="mt-3">
					<Container fluid>
						<Row>
							<ListKategori
								changeCategories={changeCategories}
							/>
							<Col>
								<h4>
									<strong>Daftar Produk</strong>
								</h4>
								<hr />
								<Row>
									{menus && menus.map((menu) => (
										<Menus 
											key={menu.id}
											nama={menu.nama}
											gambar={menu.gambar}
											harga={menu.harga}
											kategori={menu.category}
										/>
									))}
								</Row>
							</Col>
							<Hasil />
						</Row>
					</Container>
				</div>
			</div>
		</>
	);
};

export default App;
