import React from "react";
import {
	Modal,
	Button,
	Form,
} from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMinus, faPlus, faTrash } from "@fortawesome/free-solid-svg-icons";

const ModalKeranjang = (props) => {
	const { handleClose,
		showModal,
		keranjangDetail,
		tambah,
		kurang,
		jumlah,
		changeHandler,
		handleSubmit,
		totalHarga,

	} = props

	if (keranjangDetail) {
		return (
			<Modal show={showModal} onHide={handleClose}>
				<Modal.Header closeButton>
					<Modal.Title>
						{keranjangDetail.product.nama} {""}
						<strong>
							<h1>ramadhan</h1>
						</strong>
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<Form>
						<Form.Group>
							<Form.Label>
								<p>Total Harga</p>
								<strong>ramadhan</strong>
							</Form.Label>
						</Form.Group>
						<Form.Group controlId="exampleForm.ControlInput1">
							<Form.Label>Jumlah Pesan</Form.Label>
							<br/>
							<Button variant="info" className="mr-2">
								<FontAwesomeIcon icon={faMinus}/>
							</Button>
							<Button variant="info" className="ml-2">
								<FontAwesomeIcon icon={faPlus}/>
							</Button>
						</Form.Group>
						<div>
							<Button variant="secondary" type="submit"  onClick={handleClose} className="mr-2">
								Simpan
							</Button>
							<Button variant="danger" >
								<FontAwesomeIcon icon={faTrash} className="mr-1" />Hapus Pesanan
							</Button>
						</div>
					</Form>
				</Modal.Body>
			</Modal>
		);
	} else {
		return false
	}
};

export default ModalKeranjang;
