import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { apikey, ts, hash } from './helper';

const ComicsUI = (props) => {
	const {comicId} = props
	const [comics, setComics] = useState([])

	useEffect(() => {
		axios(`https://gateway.marvel.com:443/v1/public/characters/${comicId}/comics?apikey=${apikey}&ts=${ts}&hash=${hash}`)
		.then(response => setComics(response.data.data.results))
		.catch(err => console.log(err))
	}, [])

	console.log(comics)

	return(
		<>
			{
				<div className="card" style={{width: "18rem"}}>
				<img className="card-img-top" src="" alt="Card image cap" />
				<div className="card-body">
					<h3>Nama Comic</h3>
					<p className="card-text">Pada suatu hari yang cerah, doktor setrange membeli buah jeruk untuk si hulk</p>
					</div>
				</div>
			}
		</>
	)
}

export default ComicsUI
