import React, { useState, useEffect } from "react";
import { Row, Container } from "react-bootstrap";
import { NavbarComp, ListKategori, Hasil, DaftarProduk } from "./utils/index";
import { API_URL } from "./utils/constants";
import axios from "axios";

export const UserContext = React.createContext()

const App = () => {
	const [menus, setMenus] = useState();
	const [selectedCategories, setSelectedCategories] = useState("Makanan");
	const [keranjangs, setKeranjangs] = useState([]);

	useEffect(() => {
		axios
			.get(API_URL + "products?category.nama=" + selectedCategories)
			.then((response) => {
				// handle success
				setMenus(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, []);

	const changeCategories = (value) => {
		setSelectedCategories(value);
		setMenus();

		axios
			.get(API_URL + "products?category.nama=" + value)
			.then((response) => {
				// handle success
				setMenus(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	};

	const masukKeranjang = (value) => {
		axios
			.post(API_URL + "keranjangs")
			.then((response) => {
				swal("Berhasil", "Pesanan Masuk Keranjang", "success");
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}

	return (
		<div className="App">
			<NavbarComp />
			<div className="mt-3">
				<Container fluid>
					<Row>
						<ListKategori changeCategories={changeCategories} selectedCategories={selectedCategories} />
						{/* passing method masukKeranjang to Menus Component using ReactContext*/}
						<UserContext.Provider value={masukKeranjang}>
							<DaftarProduk menus={menus}/>
						</UserContext.Provider>
						<Hasil />
					</Row>
				</Container>
			</div>
		</div>
	);
};

export default App;
