import React, { useState } from "react";
import {
	Modal,
	Button,
	Form,
} from "react-bootstrap";
import numberWithCommas from "../utils/utils";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMinus, faPlus, faTrash } from "@fortawesome/free-solid-svg-icons";

const ModalKeranjang = (props) => {
	const { 
		showModal,
	} = props

	const [keranjangDetail, setKeranjangDetail] = useState();
	const [jumlah, setJumlah] = useState();
	const [keterangan, setKeterangan] = useState();
	const [totalHarga, setTotalharga] = useState();

	const handleClose = () => {
		setShowModal(false)
	}

	const tambah = () => {
		setJumlah(jumlah+1)
		setTotalharga(keranjangDetail.product.harga*(jumlah+1))
	}

	const kurang = () => {
		if (jumlah !== 1) {
			setJumlah(jumlah-1)
			setTotalharga(keranjangDetail.product.harga*(jumlah-1))
		}
	}

	const changeHandler = (event) => {
		setKeterangan(event.target.value)
	}

	const handleSubmit = (event) => {
		event.preventDefault()

		const data = {
			jumlah: jumlah,
			total_harga: totalHarga,
			product: keranjangDetail.product,
			keterangan: keterangan
		};

		axios
			.put(API_URL + "keranjangs/" + keranjangDetail.id, data)
			.then((response) => {
				getListKeranjang()
				swal({
					title: "Berhasil!",
					text: "Pesanan Berhasil Diupdate",
					icon: "success",
					button: false,
					timer: 1000,
				});
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}

	const hapusPesanan = (id) => {
		handleClose()
		axios
			.delete(API_URL + "keranjangs/" + id)
			.then((response) => {
				getListKeranjang()
				swal({
					title: "Berhasil!",
					text: "Pesanan Berhasil Dihapus",
					icon: "error",
					button: false,
					timer: 1000,
				});
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}

	if (keranjangDetail) {
		return (
			<Modal show={showModal} onHide={handleClose}>
				<Modal.Header closeButton>
					<Modal.Title>
						{keranjangDetail.product.nama} {""}
						<strong>
							({numberWithCommas(keranjangDetail.product.harga)})
						</strong>
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<Form onSubmit={handleSubmit}>
						<Form.Group>
							<Form.Label>
								<p>Total Harga</p>
								<strong>{numberWithCommas(totalHarga)}</strong>
							</Form.Label>
						</Form.Group>
						<Form.Group controlId="exampleForm.ControlInput1">
							<Form.Label>Jumlah Pesan</Form.Label>
							<br/>
							<Button variant="info" className="mr-2" onClick={() => kurang()}>
								<FontAwesomeIcon icon={faMinus}/>
							</Button>
							<strong>{jumlah}</strong>
							<Button variant="info" className="ml-2" onClick={() => tambah()}>
								<FontAwesomeIcon icon={faPlus}/>
							</Button>
						</Form.Group>
						<Form.Group controlId="exampleForm.ControlTextarea1">
							<Form.Label>Keterangan :</Form.Label>
							<Form.Control as="textarea" rows={3}
							name="keterangan"
							placeholder="Contoh: Nasi Setengah, Kuah Dipisah"
							value={keterangan}
							onChange={(event) => {changeHandler(event)}}/>
						</Form.Group>
						<Button variant="secondary" type="submit"  onClick={handleClose}>
							Simpan
						</Button>
					</Form>
				</Modal.Body>

				<Modal.Footer>
					<Button variant="danger" onClick={() => {hapusPesanan(keranjangDetail.id)}} >
						<FontAwesomeIcon icon={faTrash} className="mr-1" />Hapus Pesanan
					</Button>
				</Modal.Footer>
			</Modal>
		);
	} else {
		return false
	}
};

export default ModalKeranjang;
