import React, { useState } from "react";
import { Col, Row, ListGroup, Badge, Modal, Button } from 'react-bootstrap';
import { TotalBayar } from "../utils/index";
import numberWithCommas from "../utils/utils"

const ListKategori = ({keranjangs}) => {

	const [showModal, setShowModal] = useState(false);
	const [keranjangDetail, setKeranjangDetail] = useState(false);
	const [jumlah, setJumlah] = useState(false);
	const [keterangan, setKeterangan] = useState(false);

	const handleShow = (menuKeranjang) => {
		setShowModal(true)
		setKeranjangDetail(menuKeranjang)
	}

	const handleClose = () => {
		setShowModal(false)
	}

	return(
		<Col md={3} mt="2">
			<h4><strong>Hasil</strong></h4>
			<hr />
			{keranjangs.length !== 0 && (
				<ListGroup variant="flush">
					{ keranjangs.map((menuKeranjang) => (
						<ListGroup.Item key={menuKeranjang.id} onClick={() => {handleShow(menuKeranjang)}}>
							<Row>
								<Col xl={2}>
									<h6>
										<Badge variant="primary">{menuKeranjang.jumlah}</Badge>
									</h6>
								</Col>
								<Col>
									<h6><strong>{menuKeranjang.product.nama}</strong></h6>
									<h6>{numberWithCommas(menuKeranjang.product.harga)}</h6>
								</Col>
								<Col>
									<h6><strong>{numberWithCommas(menuKeranjang.total_harga)}</strong></h6>
								</Col>
							</Row>
						</ListGroup.Item>
					))}

					<Modal show={showModal} onHide={handleClose}>
				        <Modal.Header closeButton>
				          <Modal.Title>Modal heading</Modal.Title>
				        </Modal.Header>
				        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
				        <Modal.Footer>
				          <Button variant="secondary" onClick={handleClose}>
				            Close
				          </Button>
				        </Modal.Footer>
				     </Modal>
				</ListGroup>
			)}
			<TotalBayar keranjangs={keranjangs}/>
		</Col>
	)
}

export default ListKategori;
