import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavComp from "./NavComp";
import DaftarMenu from "./DaftarMenu";
import Keranjang from "./Keranjang";

const App = () => {

	// changing categories
	const [kategoriAktif, setKategoriAktif] = useState("Makanan");
	const [kategoris, setKategoris] = useState();

	// handling keranjang
	const [jumlahPesan, setJumlahPesan] = useState(0);
	const [totalHarga, setTotalHarga] = useState(0);
	const [keterangan, setKeterangan] = useState("jangan pakai lama ya mazzzz")

	// query kategori
	useEffect(() => {
		axios
			.get("http://localhost:3004/categories")
			.then((response) => {
				// handle success
				setKategoris(response.data)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, [kategoriAktif]);

	const gantiKategori = (kategori) => {
		setKategoriAktif(kategori)
	}

	const tambahKeranjang = (kategori) => {
		axios
			.get("http://localhost:3004/keranjangs")
			.then((response) => {
				// handle success
				console.log(kategori)
				response.data.length && response.data.map((keranjangItems) => {
					if(keranjangItems.product.id === kategori.id) {
						console.log(keranjangItems)
						const keranjang = {
							jumlah: keranjangItems.jumlah+1,
							total_harga: keranjangItems.total_harga + kategori.harga,
							product: keranjangItems.product,
						};

						console.log(keranjang)
					}
				})
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}

	return (
		<React.Fragment>
			<NavComp
				kategoris={kategoris}
				gantiKategori={gantiKategori}
				kategoriAktif={kategoriAktif}
			/>
			<div className="container-menu">
				<DaftarMenu kategoriAktif={kategoriAktif} tambahKeranjang={tambahKeranjang}/>
				<Keranjang jumlahPesan={jumlahPesan} />
			</div>
		</React.Fragment>
	)
};

export default App;
