import React, {useState, useEffect, useRef} from 'react'
import HeroUI from './HeroUI'
import ComicsUI from './ComicsUI'
import Kaki from './Kaki'
import axios from 'axios'
import { apikey, ts, hash } from './helper'

const Marvel = () => {
	const [hero, setHero] = useState()
	const [search, setSearch] = useState()
	const query = useRef()

	// Getting Hero
	useEffect(() => {
		query.current.focus()
		axios(`https://gateway.marvel.com:443/v1/public/characters?apikey=${apikey}&ts=${ts}&hash=${hash}&name=${search}`)
		.then(response => {
			setHero(response.data.data.results[0])
		})
		.catch(err => console.log(err))
	}, [search])

	const handleInput = (event) => {
		event.preventDefault()
		setSearch(query.current.value)
		query.current.value = ''
	}
	if (hero !== undefined) {
		return(
			<>
				<nav>
					<form onSubmit={handleInput}>
						<div>
							<input ref={query}/>
						</div>
						<button>Cari</button>
					</form>
				</nav>
				<HeroUI
					name={hero.name}
					thumbnail={hero.thumbnail.path} 
					description={hero.description} 
				/>
				<ComicsUI />
				<Kaki urls={hero.urls}/>
			</>
		)
	} else {
		return(
			<>
				<nav>
					<form onSubmit={handleInput}>
						<div>
							<input ref={query}/>
						</div>
						<button>Cari</button>
					</form>
				</nav>
			</>
		)
	}

}

export default Marvel
