import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { apikey, ts, hash } from './helper';

const ComicsUI = (props) => {
	const {comicId} = props
	const [comics, setComics] = useState([])

	useEffect(() => {
		axios(`https://gateway.marvel.com:443/v1/public/characters/${comicId}/comics?apikey=${apikey}&ts=${ts}&hash=${hash}`)
		.then(response => setComics(response.data.data.results))
		.catch(err => console.log(err))
	}, [])

	console.log(comics)

	return(
		<>
			<div className="row row-cols-1 row-cols-md-4 g-4">
				{
					comics.map(comic => {
						return(
							<div key={comic.id} className="col">
						    <div className="card w-100 p-3">
						      <img
						        src={comic.thumbnail.path + "/portrait_xlarge.jpg"}
						        className="card-img-top h-100"
						        alt="..."
						      />
						      <div className="card-body">
						        <h4 className="card-title">{comic.title}</h4>
						      </div>
						    </div>
						  </div>
						)
					})
				}
			</div>
		</>
	)
}

export default ComicsUI
