import React, { useState } from "react";
import { Col, Row, ListGroup, Badge } from 'react-bootstrap';
import { TotalBayar } from "../utils/index";
import numberWithCommas from "../utils/utils"
import { ModalKeranjang} from "../utils/index";
import { API_URL } from "../utils/constants";
import axios from "axios";
import swal from "sweetalert";

const Hasil = ({keranjangs, listGetKeranjang}) => {

	const [showModal, setShowModal] = useState();
	const [keranjangDetail, setKeranjangDetail] = useState();
	const [jumlah, setJumlah] = useState();
	const [keterangan, setKeterangan] = useState();
	const [totalHarga, setTotalharga] = useState();

	const handleShow = (menuKeranjang) => {
		setShowModal(true)
		setKeranjangDetail(menuKeranjang)
		setKeterangan(menuKeranjang.keterangan)
		setJumlah(menuKeranjang.jumlah)
		setTotalharga(menuKeranjang.total_harga)
	}

	const handleClose = () => {
		setShowModal(false)
	}

	const tambah = () => {
		setJumlah(jumlah+1)
		setTotalharga(keranjangDetail.product.harga*(jumlah+1))
	}

	const kurang = () => {
		if (jumlah !== 1) {
			setJumlah(jumlah-1)
			setTotalharga(keranjangDetail.product.harga*(jumlah-1))
		}
	}

	const changeHandler = (event) => {
		setKeterangan(event.target.value)
	}

	const handleSubmit = (event) => {
		event.preventDefault()

		const data = {
			jumlah: jumlah,
			total_harga: totalHarga,
			product: keranjangDetail.product,
			keterangan: keterangan
		};

		axios
			.put(API_URL + "keranjangs/" + keranjangDetail.id, data)
			.then((response) => {
					swal({
						title: "Berhasil Diupdate!",
						icon: "success",
						button: false,
						timer: 1000,
					});
				})
				.catch((error) => {
					// handle error
					console.log(error);
				});
	}

	return(
		<Col md={3} mt="2">
			<h4><strong>Hasil</strong></h4>
			<hr />
			{keranjangs.length !== 0 && (
				<ListGroup variant="flush">
					{ keranjangs.map((menuKeranjang) => (
						<ListGroup.Item key={menuKeranjang.id} onClick={() => {handleShow(menuKeranjang)}} style={{cursor: 'pointer'}}>
							<Row >
								<Col xl={2} >
									<h6>
										<Badge variant="primary">{menuKeranjang.jumlah}</Badge>
									</h6>
								</Col>
								<Col>
									<h6><strong>{menuKeranjang.product.nama}</strong></h6>
									<h6>{numberWithCommas(menuKeranjang.product.harga)}</h6>
								</Col>
								<Col>
									<h6>
										<strong>
											{numberWithCommas(menuKeranjang.total_harga)}
										</strong>
									</h6>
								</Col>
							</Row>
						</ListGroup.Item>
					))}
					<ModalKeranjang
					handleClose={handleClose}
					showModal={showModal}
					keranjangDetail={keranjangDetail}
					keterangan={keterangan}
					tambah={tambah}
					kurang={kurang}
					jumlah={jumlah}
					changeHandler={changeHandler}
					handleSubmit={handleSubmit}
					totalHarga={totalHarga}/>
				</ListGroup>
			)}
			<TotalBayar keranjangs={keranjangs}/>
		</Col>
	)
}

export default Hasil;
