import React, { useState, useEffect } from 'react';
import axios from 'axios'

const Keranjang = ({jumlahPesan}) => {

	const [keranjangPost, setKeranjangPost] = useState()

	useEffect(() => {
		axios
			.get("http://localhost:3004/keranjangs")
			.then((response) => {
				// handle success
				setKeranjangPost(response.data)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});				
	}, []);

	return (
		<div className="container-keranjang">
			{keranjangPost && keranjangPost.map(keranjangs => {
				return(
					<div>
						<span className="keranjang-top">
							<span className="tambah-kurang">
								<span>-</span>
								<div>{keranjangs.jumlah}</div>
								<span>+</span>
							</span>
							<h1>{keranjangs.product.nama}</h1>
						</span>
						<h1>Rp {new Intl.NumberFormat("de-DE").format(keranjangs.total_harga)}</h1>
					</div>
				)
			})}
		</div>
	)
};

export default Keranjang;
