import React, { useState } from "react";
import {
	Col,
	Row,
	ListGroup,
	Badge,
	Modal,
	Button,
	Form,
} from "react-bootstrap";
import numberWithCommas from "../utils/utils";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

const ModalKeranjang = ({ handleClose, showModal, keranjangDetail }) => {
	if (keranjangDetail) {
		return (
			<Modal show={showModal} onHide={handleClose}>
				<Modal.Header closeButton>
					<Modal.Title>
						{keranjangDetail.product.nama} {""}
						<strong>
							({numberWithCommas(keranjangDetail.product.harga)})
						</strong>
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<Form>
						<Form.Group controlId="exampleForm.ControlInput1">
							<Form.Label>Total Harga</Form.Label>
							<p className="mt-4">{keranjangDetail.total_harga}</p>
						</Form.Group>
						<Form.Group controlId="exampleForm.ControlTextarea1">
							<Form.Label>Keterangan :</Form.Label>
							<Form.Control as="textarea" rows={3} />
						</Form.Group>
					</Form>
				</Modal.Body>
				<Modal.Footer>
					<Button variant="secondary" onClick={handleClose}>
						Close
					</Button>
					<Button variant="danger" onClick={handleClose}>
						<FontAwesomeIcon icon={faTrash} className="mr-1" />Hapus Pesanan
					</Button>
				</Modal.Footer>
			</Modal>
		);
	} else {
		return (
			<Modal show={showModal} onHide={handleClose}>
				<Modal.Header closeButton>
					<Modal.Title>Kosong</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					Woohoo, you're reading this text in a modal!
				</Modal.Body>
				<Modal.Footer>
					<Button variant="secondary" onClick={handleClose}>
						Close
					</Button>
				</Modal.Footer>
			</Modal>
		);
	}
};

export default ModalKeranjang;
