import React, { useState, useEffect } from 'react';
import axios from 'axios'
import NavComp from "./NavComp"
import DaftarMenu from "./DaftarMenu"

const App = () => {

	const [aktif, setAktif] = useState("Makanan");
	const [kategoris, setKategoris] = useState();

	useEffect(() => {
		axios
			.get("http://localhost:3004/categories")
			.then((response) => {
				// handle success
				setKategoris(response.data)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, [aktif])

	const gantiKategori = (kategori) => {
		setAktif(kategori)
	}

	const tambahKeranjang = (kopet) => {
		console.log(kopet)
	}

	return (
		<React.Fragment>
			<NavComp
				kategoris={kategoris}
				gantiKategori={gantiKategori}
				aktif={aktif}
			/>
			<div className="container-menu">
				<DaftarMenu aktif={aktif} tambahKeranjang={tambahKeranjang}/>
				<h1 className="pesan">booked</h1>
			</div>
		</React.Fragment>
	)
};

export default App;
