import React, {useState, useEffect, useRef} from 'react'
import axios from 'axios'
import { apikey, ts, hash } from './helper'

const Marvel = () => {
	const [hero, setHero] = useState()
	const [search, setSearch] = useState()
	const query = useRef()

	useEffect(() => {
		query.current.focus()
		axios(`https://gateway.marvel.com:443/v1/public/characters?apikey=${apikey}&ts=${ts}&hash=${hash}&name=${search}`)
		.then(response => setHero(response.data.data.results))
		.catch(err => console.log(err))
	}, [search])

	const handleInput = (event) => {
		event.preventDefault()
		setSearch(query.current.value)
		query.current.value = ''
	}

	return(
		<>
			<form onSubmit={handleInput}>
				<div>
					<input ref={query}/>
				</div>
				<button>Cari</button>
			</form>
			 <div>
        {hero.map((he) => (
          // key salah
          <Marvel
            key={he.name}
            name={he.name}
            des={he.description}
            img={he.thumbnail.path}
          />
        ))}
      </div>
		</>
	)
}

export default Marvel
