import React, { useState, useEffect } from 'react';
import axios from 'axios';
import swal from "sweetalert";
import NavComp from "./NavComp";
import DaftarMenu from "./DaftarMenu";
import Keranjang from "./Keranjang";

const App = () => {

	// changing categories
	const [kategoriAktif, setKategoriAktif] = useState("Makanan");
	const [kategoris, setKategoris] = useState();

	// handling keranjang
	// const [jumlahPesan, setJumlahPesan] = useState(0);
	// const [totalHarga, setTotalHarga] = useState(0);
	// const [keterangan, setKeterangan] = useState("jangan pakai lama ya mazzzz")
	const [keranjangPost, setKeranjangPost] = useState();

	// query kategori
	useEffect(() => {
		axios
			.get("http://localhost:3004/categories")
			.then((response) => {
				// handle success
				setKategoris(response.data)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});

			getKeranjang()
	}, []);

	const gantiKategori = (kategori) => {
		setKategoriAktif(kategori)
	}

	const getKeranjang = () => {
		axios
			.get("http://localhost:3004/keranjangs")
			.then((response) => {
				// handle success
				setKeranjangPost(response.data)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});	
	}

	const tambahKeranjang = (kategori) => {
		axios
			.get("http://localhost:3004/keranjangs?product.id=" + kategori.id)
			.then((response) => {
				// handle success
				if(response.data.length) {
					const keranjang = {
						jumlah: response.data[0].jumlah+1,
						total_harga: response.data[0].total_harga + kategori.harga,
						product: response.data[0].product
					};

					axios
						.put("http://localhost:3004/keranjangs/" +response.data[0].id, keranjang)
						.then((response) => {
							swal({
								title: "Berhasil!",
								text: "Pesanan Berhasil Masuk Keranjang",
								icon: "success",
								button: false,
								timer: 1000,
							});
						})
						.catch((error) => {
							// handle error
							console.log(error);
						});
						getKeranjang()
						console.log("render")
				} else {
					const keranjang = {
						jumlah: 1,
						total_harga: kategori.harga,
						product: kategori
					};
					axios
						.post("http://localhost:3004/keranjangs", keranjang)
						.then((response) => {
							swal({
								title: "Berhasil!",
								text: "Pesanan Berhasil Masuk Keranjang",
								icon: "success",
								button: false,
								timer: 1000,
							});
						})
						.catch((error) => {
							// handle error
							console.log(error);
						});
						getKeranjang()
						console.log("render")
				}
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}

	return (
		<React.Fragment>
			<NavComp
				kategoris={kategoris}
				gantiKategori={gantiKategori}
				kategoriAktif={kategoriAktif}
			/>
			<div className="container-menu">
				<DaftarMenu kategoriAktif={kategoriAktif} tambahKeranjang={tambahKeranjang}/>
				<Keranjang keranjangPost={keranjangPost}/>
			</div>
		</React.Fragment>
	)
};

export default App;
