import React from "react";
import { Col, Row, Button } from 'react-bootstrap';
import numberWithCommas from "../utils/utils"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";

const TotalBayar = ({ keranjangs }) => {
	const totalBayar = keranjangs.reduce((result, item) => {
		return result + item.total_harga
	}, 0);

	return (
		<div className="fixed-bottom">
			<Row>
				<Col sm={{span: 3, offset: 9}} className="px-4">
					<h4>Total Harga: <strong className="float-right mr-2">{numberWithCommas(totalBayar)}</strong></h4>
					<Button variant="primary" block><FontAwesomeIcon icon={faShoppingCart} className="mr-1"/><strong>BAYAR</strong></Button>
				</Col>
			</Row>
		</div>
	);
};

export default TotalBayar;
