import React, { useState } from "react";
import { Col, Row, ListGroup, Badge } from 'react-bootstrap';
import { TotalBayar } from "../utils/index";
import numberWithCommas from "../utils/utils"
import { ModalKeranjang} from "../utils/index";

const ListKategori = ({keranjangs}) => {

	const [showModal, setShowModal] = useState(false);
	const [keranjangDetail, setKeranjangDetail] = useState(false);
	const [jumlah, setJumlah] = useState(keranjangDetail.jumlah);
	const [keterangan, setKeterangan] = useState(false);

	const handleShow = (menuKeranjang) => {
		setShowModal(true)
		setKeranjangDetail(menuKeranjang)
		setKeterangan(menuKeranjang.keterangan)
	}

	const handleClose = () => {
		setShowModal(false)
	}

	const tambah = () => {
		setJumlah(jumlah+1)
	}

	const kurang = () => {
		if (jumlah !== 1) {
			setJumlah(jumlah-1)
		}
	}

	return(
		<Col md={3} mt="2">
			<h4><strong>Hasil</strong></h4>
			<hr />
			{keranjangs.length !== 0 && (
				<ListGroup variant="flush">
					{ keranjangs.map((menuKeranjang) => (
						<ListGroup.Item key={menuKeranjang.id} onClick={() => {handleShow(menuKeranjang)}} style={{cursor: 'pointer'}}>
							<Row >
								<Col xl={2} >
									<h6>
										<Badge variant="primary">{menuKeranjang.jumlah}</Badge>
									</h6>
								</Col>
								<Col>
									<h6><strong>{menuKeranjang.product.nama}</strong></h6>
									<h6>{numberWithCommas(menuKeranjang.product.harga)}</h6>
								</Col>
								<Col>
									<h6>
										<strong>
											{numberWithCommas(menuKeranjang.total_harga)}
										</strong>
									</h6>
								</Col>
							</Row>
						</ListGroup.Item>
					))}
					<ModalKeranjang
					handleClose={handleClose}
					showModal={showModal}
					keranjangDetail={keranjangDetail}
					keterangan={keterangan}
					tambah={tambah}
					kurang={kurang}
					jumlah={jumlah}/>
				</ListGroup>
			)}
			<TotalBayar keranjangs={keranjangs}/>
		</Col>
	)
}

export default ListKategori;
