import React from "react";
import { useHistory } from "react-router";
import { Col, Row, Button } from "react-bootstrap";
import numberWithCommas from "../utils/utils";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { API_URL } from "../utils/constants";
import swal from "sweetalert";

const TotalBayar = ({ keranjangs }) => {
	let history = useHistory();

	const submitTotalBayar = (totalBayar) => {
		const pesanan = {
			total_bayar: totalBayar,
			menus: keranjangs,
		};

		if (pesanan.menus.length === 0) {
			swal({
				title: "Keranjang Anda Kosong!",
				text: "Silahkan Pilih Menu Sebelum Bayar",
				button: false,
				timer: 1000,
			});
		} else {
			axios
				.post(API_URL + "pesanans", pesanan)
				.then((response) => {
					// handle success
					history.push("/sukses");
				})
				.catch((error) => {
					// handle error
					console.log(error);
				});
			sessionStorage.setItem("login", true);
		}
	};

	const totalBayar = keranjangs.reduce((result, item) => {
		return result + item.total_harga;
	}, 0);

	return (
		<>
			<div className="fixed-bottom d-none d-md-block">
				<Row>
					<Col sm={{ span: 3, offset: 9 }} className="px-4">
						<h4>
							Total Harga:{" "}
							<strong className="float-right mr-2">
								{numberWithCommas(totalBayar)}
							</strong>
						</h4>
						<Button
							variant="primary"
							block
							className="mb-2 mt-2 mr-2"
							size="lg"
							onClick={() => {
								submitTotalBayar(totalBayar);
							}}
						>
							<FontAwesomeIcon
								icon={faShoppingCart}
								className="mr-1"
							/>
							<strong>Bayar</strong>
						</Button>
					</Col>
				</Row>
			</div>

			<div className="d-sm-block d-md-none">
			<Row>
				<Col sm={{ span: 3, offset: 9 }} className="px-4">
					<h4>
						Total Harga:{" "}
						<strong className="float-right mr-2">
							{numberWithCommas(totalBayar)}
						</strong>
					</h4>
					<Button
						variant="primary"
						block
						className="mb-2 mt-2 mr-2"
						size="lg"
						onClick={() => {
							submitTotalBayar(totalBayar);
						}}
					>
						<FontAwesomeIcon
							icon={faShoppingCart}
							className="mr-1"
						/>
						<strong>Bayar</strong>
					</Button>
				</Col>
			</Row>
		</div>
		</>
	);
};

export default TotalBayar;
