import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios'

const NavComp = () => {
	const kategoris = [
		{
			id: 1,
			nama: "Makanan"
		},
		{
			id: 2,
			nama: "Minuman"
		},
		{
			id: 3,
			nama: "Camilan"
		}
	]

	const [aktif, setAktif] = useState("Makanan");

	useEffect(() => {
		axios
			.get("http://localhost:3004/" + "categories")
			.then((response) => {
				// handle success
				console.log(response)
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	})

	const gantiKategori = (kategori) => {
		setAktif(kategori)
	}

	return(
		<div className="container">
			<div>
				{kategoris && kategoris.map(kategori => {
					return(
						<button
							key={kategori.id}
							onClick={() => {gantiKategori(kategori.nama)}}
							className={aktif === kategori.nama ? "aktif" : undefined}
							>{kategori.nama}
						</button>
					)
				})}
			</div>
			<h1 className="kategori">Kategori Pesanan</h1>
		</div>
	)
}

export default NavComp;
