import React from "react";
import { Card, Col } from "react-bootstrap";

const App = ({ nama, gambar, harga }) => {
	return (
		<Col md={4} xs={6} className="mb-4" >
			<Card>
				<Card.Img variant="top" src="holder.js/100px180" />
				<Card.Body>
					<Card.Title>{nama}</Card.Title>
					<Card.Text className=" my-4">Rp {new Intl.NumberFormat('de-DE').format(harga)}</Card.Text>
				</Card.Body>
			</Card>
		</Col>
	);
};

export default App;
