import React, { useState, useEffect } from "react";
import { Row, Container } from "react-bootstrap";
import { ListKategori, Hasil, DaftarProduk } from "../utils/index";
import { API_URL } from "../utils/constants";
import axios from "axios";
import swal from "sweetalert";

export const UserContext = React.createContext();

const App = () => {
	const [menus, setMenus] = useState();
	const [selectedCategories, setSelectedCategories] = useState("Makanan");
	const [keranjangs, setKeranjangs] = useState([]);

	useEffect(() => {
		axios
			.get(API_URL + "products?category.nama=" + selectedCategories)
			.then((response) => {
				// handle success
				setMenus(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
			getListKeranjang()
	}, []);

	const getListKeranjang = () => {
		axios
			.get(API_URL + "keranjangs")
			.then((response) => {
				setKeranjangs(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}

	const changeCategories = (value) => {
		setSelectedCategories(value);
		setMenus();

		axios
			.get(API_URL + "products?category.nama=" + value)
			.then((response) => {
				// handle success
				setMenus(response.data);
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	};

	const masukKeranjang = (value) => {
		axios
			.get(API_URL + "keranjangs?product.id=" + value.id)
			.then((response) => {
				if (response.data.length === 0) {
					const keranjang = {
						jumlah: 1,
						total_harga: value.harga,
						product: value,
					};

					axios
						.post(API_URL + "keranjangs", keranjang)
						.then((response) => {
							getListKeranjang()
							swal({
								title: "Berhasil!",
								text: "Pesanan Berhasil Masuk Keranjang",
								icon: "success",
								button: false,
								timer: 1000,
							});
						})
						.catch((error) => {
							// handle error
							console.log(error);
						});
				} else {
					const keranjang = {
						jumlah: response.data[0].jumlah+1,
						total_harga: response.data[0].total_harga + value.harga,
						product: value,
					};

					axios
						.put(API_URL + "keranjangs/" +response.data[0].id, keranjang)
						.then((response) => {
							getListKeranjang()
							swal({
								title: "Berhasil!",
								text: "Pesanan Berhasil Masuk Keranjang",
								icon: "success",
								button: false,
								timer: 1000,
							});
						})
						.catch((error) => {
							// handle error
							console.log(error);
						});
				}
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	};

	return (
		<div className="mt-3">
			<Container fluid>
				<Row>
					<ListKategori
						changeCategories={changeCategories}
						selectedCategories={selectedCategories}
					/>
					{/* passing method masukKeranjang to Menus Component with ReactContext*/}
					<UserContext.Provider value={masukKeranjang}>
						<DaftarProduk menus={menus} />
					</UserContext.Provider>
					<Hasil keranjangs={keranjangs}/>
				</Row>
			</Container>
		</div>
	);
};

export default React.memo(App);
