import React from "react";
import { Card, Col } from "react-bootstrap";
import { UserContext } from "../App";

const Menus = (value) => {
	const { id, nama, gambar, harga, kategori } = value;
	/* imported function from App Component*/
	const masukKeranjang = UserContext._currentValue;

	return (
		<Col md={4} xs={6} className="mb-4">
			<Card className="shadow" onClick={() => masukKeranjang(value)}>
				<Card.Img
					variant="top"
					src={"asset/" + kategori.nama.toLowerCase() + "/" + gambar}
				/>
				<Card.Body>
					<Card.Title>{nama}</Card.Title>
					<Card.Text>
						Rp {new Intl.NumberFormat("de-DE").format(harga)}
					</Card.Text>
				</Card.Body>
			</Card>
		</Col>
	);
};

export default Menus;
