import React, { useEffect } from "react";
import { API_URL } from "../utils/constants";
import axios from "axios";
import { useHistory } from "react-router"
import { Button, Image } from "react-bootstrap";
import { Link } from "react-router-dom";

const Sukses = () => {
	let history = useHistory();
	const isLogin = sessionStorage.getItem("login");

	// cek apakah user menekan tombol bayar
	if (isLogin === null) {
		history.push('/');
	}

	const hapusSession = () => {
		console.log("ggg")
	}

	useEffect(() => {
		axios
			.get(API_URL + "keranjangs")
			.then((response) => {
				const keranjangs = response.data
				keranjangs.map(item => {
					return axios.delete(API_URL + "keranjangs/" + item.id)
				})
			})
			.catch((error) => {
				// handle error
				console.log(error);
			});
	}, [])

	return (
		<div className="mt-4 text-center">
			<Image src="asset/success.png" width="500"/>
			<h2><strong>Pesanan Berhasil</strong></h2>
			<p>Terimakasih Sudah Memesan</p>
			<Button variant="primary" as={Link} to="/" onClick={() => {hapusSession}}>
				Kembali
			</Button>
		</div>
	)
};

export default Sukses;
